    /**
     * @param mMap      Google map
     * @param zoomLevel Zoom level
     */
    public void addMarkers(GoogleMap mMap, float zoomLevel) {

        double lat = mMap.getCameraPosition().target.latitude;
        double lon = mMap.getCameraPosition().target.longitude;

        double latMax = lat + 3;
        double latMin = lat - 3;
        double longMax = lon + 5;
        double longMin = lon - 4;

        for (MarkerOptions option : this.markerOptions) {

            if (zoomLevel <= 5) {
                option.icon(image);
            } else if (zoomLevel <= 7) {
                option.icon(image2);
            } else if (zoomLevel <= 12) {
                option.icon(image3);
            } else {
                option.icon(null);

            }

            LatLng position = option.getPosition();
            double latitude = position.latitude;
            double longitude = position.longitude;


            if ((latitude < latMax) && (latitude > latMin) && (longitude < longMax) && (longitude > longMin)) {
                mMap.addMarker(option);
            }
        }
    }

    /**
     * @param mMap      Google map
     * @param zoomLevel Zoom level
     */
    public void addMarkers(GoogleMap mMap, float zoomLevel, Collection<MarkerOptions> markerOptions) {
        this.markerOptions = markerOptions;
        addMarkers(mMap, zoomLevel);
    }
